		If (((preco/1) '= (preco)) || (preco < 0)){
			Write !!
			THROW ##class(%Exception.General).%New("Preço inválido","128",,"O preço precisa ser um número maior que zero!")
		}
	 	Read !!, "	Digite o id do setor: ", idSetor
